package com.limijiaoyin.utils;import java.io.BufferedInputStream;import java.io.FileInputStream;import java.io.IOException;public class FileUtils {    public static byte[] readFileImage(String filename) throws IOException {        BufferedInputStream bufferedInputStream = new BufferedInputStream(                new FileInputStream(filename));        int len = bufferedInputStream.available();        byte[] bytes = new byte[len];        int r = bufferedInputStream.read(bytes);        if (len != r) {            bytes = null;            throw new IOException("读取文件不正确");        }        bufferedInputStream.close();        return bytes;    }}